import * as React from 'react';
import {useEffect} from "react";
import { View, Text, StatusBar} from 'react-native';

/*https://www.digitalocean.com/community/tutorials/react-countdown-timer-react-hooks*/

function AutoTimeout(){
    useEffect(() => {
        setTimeout(() => navigation.navigate('MainFunction'), 1500);
      });
      
} ;

export default class ConnectedScreen extends React.Component {    
    render(){

        <AutoTimeout />
		return(
            <View style={{ backgroundColor: '#90FF33', flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                <StatusBar barStyle="dark-content" backgroundColor="#ecf0f1" />
                <Text>You are connected</Text>
            </View>
		)
	};
}
