import * as React from 'react';
import { Button, View, TextInput, ScrollView} from 'react-native';
import '@firebase/auth';
import '@firebase/firestore';

export default class LoginScreen extends React.Component {
    
    handleLogin = () => {
        const {username,password} = this.state;
        firebase.auth().signInWithEmailandPassword(email,password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    
    
    render(){

        const [username, setusername] = React.useState('');
        const [password, setpassword] = React.useState('');

		return(
            <ScrollView>
                <View style={{ flex: 1, justifyContent: 'center', marginTop: 250, marginBottom: 10 }}>
                    <TextInput
                        placeholder="Username"
                        value={username}
                        onChangeText={setusername}
                    />
                <View style={{ flex: 1, justifyContent: 'center', marginTop:40, marginBottom: 10 }}>
                    <TextInput
                            placeholder="Password"
                            value={password}
                            onChangeText={setpassword}
                            secureTextEntry
                        />
                <View style={{ flex: 1, justifyContent: 'center', marginBottom: 10 }}>
                    <Button disabled = {(password== '')||(username == '')} title="Sign In" 
                        onPress={() => this.handleLogin() > this.props.navigation.push("Scan Now", password)}
                    />
                </View>
                </View>
                </View>
            </ScrollView>
		)
	}
}
