import * as React from 'react';
import { Button, View, TextInput, ScrollView, StyleSheet} from 'react-native';
import firebase from 'firebase/app';
import "firebase/auth";

export default class LoginScreen extends React.Component {
    
    handleLogin = () => {
        const {username,password} = this.state;
        firebase.auth().signInWithEmailandPassword(username,password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    state = {
        username: "",
        password: ""
    }   
    
    render(){
		return(
            <ScrollView>
                <View style={styles.Key_In_Username}>
                    <TextInput
                        placeholder="Username"
                        value={this.state.username}
                        onChangeText={username => this.setState({username})}
                    />
                <View style={styles.Key_In_Password}>
                    <TextInput
                            placeholder="Password"
                            value={this.state.password}
                            onChangeText={password => this.setState({password})}
                            secureTextEntry
                        />
                <View style={styles.Sign_In_Button}>
                    <Button disabled = {(this.state.password== '')||(this.state.username == '')} title="Sign In" 
                        onPress={() => this.handleLogin() > this.props.navigation.push("Scan Now", password)}
                        //onPress={() => this.props.navigation.navigate("ScanNow")}
                    />
                </View>
                </View>
                </View>
            </ScrollView>
		)
	}
}

const styles = StyleSheet.create({
    Key_In_Username: {
        flex: 1,
        justifyContent: 'center',
        marginTop: 250,
        marginBottom: 10
    },

    Key_In_Password: {
        flex: 1, 
        justifyContent: 'center',
        marginTop:40,
        marginBottom: 10 
    },

    Sign_In_Button: {
        flex: 1,
        justifyContent: 'center',
        marginBottom: 10
    }
});
