import * as React from 'react';
import CountDown from 'react-native-countdown-component';
import { View, Text, TouchableOpacity, StyleSheet} from 'react-native';

function SchedulingCountdownTimerScreen({ route, navigation }) {
    console.warn(route)
    let Text_Minutes = route.params;
  

    return (
      <View style={styles.container}>
        <Text style = {styles.Instructions}> Time before socket switch on/off</Text>
      
        <CountDown 
          size={30}
          until={Text_Minutes*60}
          onFinish={() => navigation.navigate('Times Up')}
          digitStyle={{backgroundColor: '#FFF', borderWidth: 2, borderColor: '#1CC625'}}
          digitTxtStyle={{color: '#000000'}}
          timeLabelStyle={{color: '#1CC625', fontWeight: 'bold'}}
          separatorStyle={{color: '#000000'}}
          timeToShow={['H', 'M', 'S']}
          timeLabels={{h: 'Hour', m: 'Minutes', s: 'Seconds'}}
          showSeparator
        />

        <TouchableOpacity
          style={styles.Stop_Button}
          onPress={() => navigation.navigate('Times Up')}
        >
          <Text style = {styles.Button_Text}> Stop Now </Text>
        </TouchableOpacity>
      </View>
    );
  }

  const styles = StyleSheet.create({
    Instructions: {
      textAlign: 'center',
      marginLeft: 25,
      marginRight: 25,
      marginTop: 50,
      marginBottom: 25,
      color: "#000", 
      fontWeight: "bold", 
      fontSize: 25
    },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Stop_Button: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 250,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});

export default SchedulingCountdownTimerScreen;
