import * as React from 'react';
import { Button, View, Text, StyleSheet} from 'react-native';

export default class ScanNowScreen extends React.Component {
	render(){

		return(
            <View style={styles.container}>
                <Text style = {styles.Instructions}>Scan the QR Code of the Smart Socket</Text>

                <TouchableOpacity
                        style={styles.Scan_Now_Button}
                        onPress={() => this.props.navigation.navigate("Camera")}
                    >
                        <Text style = {styles.Button_Text}> Scan Now </Text>
                </TouchableOpacity>

            </View>
		)
	}
}

const styles = StyleSheet.create({
    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Instructions: {
        marginLeft: 25,
        marginRight: 25,
        marginTop: 50,
        marginBottom: 25,
        color: "#000", 
        fontWeight: "bold", 
        fontSize: 25
    },

    Scan_Now_Button: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 250,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});
