import * as React from 'react';
import { View, TextInput, ScrollView, StyleSheet, TouchableOpacity, Text} from 'react-native';
import firebase from 'firebase/app';
import "firebase/auth";

export default class LoginScreen extends React.Component {
    
    handleLogin = () => {
        const {username,password} = this.state;
        firebase.auth().signInWithEmailandPassword(username,password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    state = {
        username: "",
        password: ""
    }   
    
    render(){
		return(
            <ScrollView>
                <View style={styles.container}>
                    <Text style = {styles.Instructions}> Login to your account</Text>
                    <TextInput
                        style={styles.Key_In_Username}
                        placeholder="Username"
                        value={this.state.username}
                        onChangeText={username => this.setState({username})}
                    />
                    <TextInput
                        style={styles.Key_In_Password}
                        placeholder="Password"
                        value={this.state.password}
                        onChangeText={password => this.setState({password})}
                        secureTextEntry
                    />
                    
                    <TouchableOpacity
                        style = {{ alignSelf: "center", marginTop: 25, marginBottom: 25}}
                        onPress={() => this.props.navigation.navigate("ForgetPassword")}
                    >
                        <Text style = {{color: "#000000", fontSize: 15}}>
                            Forgot Password? Click <Text style = {{color: "#0000FF"}}> here </Text>
                        </Text>
                    </TouchableOpacity>

                    <TouchableOpacity
                        style={styles.Sign_In_Button}
                        //onPress={() => this.handleLogin() > this.props.navigation.push("Scan Now", password)}
                        onPress={() => this.props.navigation.navigate("ScanNow")}
                        disabled = {(this.state.password== '')||(this.state.username == '')}
                    >
                        <Text style = {styles.Button_Text}> Log In </Text>
                    </TouchableOpacity>   
                </View>
            </ScrollView>
		)
	}
}

const styles = StyleSheet.create({
    Instructions: {
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 50,
        marginBottom: 25,
        color: "#000", 
        fontWeight: "bold", 
        fontSize: 25
    },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Key_In_Username: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Key_In_Password: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Sign_In_Button: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 250,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});
