import * as React from 'react';
import { Button, View, Text, TextInput, ScrollView} from 'react-native';
//import * as firebase from 'firebase';
import firebase from 'firebase/app';
import "firebase/auth";
//import { firebase } from '@react-native-firebase/auth';

export default class SignUpScreen extends React.Component {
    handleSignUp = () => {
        const {username,password} = this.state;
        firebase.auth().createUserWithEmailAndPassword(emailPhonenumber, password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    
    state = {
        fullname : "",
        username : "",
        emailPhonenumber : "",
        password : "",
        confirmpassword : ""
    }
    
    render(){
		return(
            <ScrollView>
                <View style={{ flex: 1, alignItems: 'center', marginTop: 150, marginBottom: 30 }}>
                    <Text>Fill IN THE DETAILS BELOW</Text>

                <View style={{ flex: 1, justifyContent: 'center', marginTop:40, marginBottom: 10 }}>    
                    <TextInput
                        placeholder="Full Name*"
                        value={this.state.fullname}
                        onChangeText={fullname => this.setState({fullname})}
                    />
                <View style={{ flex: 1, justifyContent: 'center', marginTop:40, marginBottom: 10 }}>
                    <TextInput
                        placeholder="Username*"
                        value={this.state.username}
                        onChangeText={username => this.setState({username})}
                    />
                <View style={{ flex: 1, justifyContent: 'center', marginTop:40, marginBottom: 10 }}>
                    <TextInput
                        placeholder="Email or Phone Number"
                        value={this.state.emailPhonenumber}
                        onChangeText={emailPhonenumber => this.setState({emailPhonenumber})}
                    />
                <View style={{ flex: 1, justifyContent: 'center', marginTop:40, marginBottom: 10 }}>
                    <TextInput
                        placeholder="Password*"
                        value={this.state.password}
                        onChangeText={password => this.setState({password})}
                        secureTextEntry
                    />
                <View style={{ flex: 1, justifyContent: 'center',marginTop:40, marginBottom: 10 }}>
                    <TextInput
                        placeholder="Confirm Password*"
                        value={this.state.confirmpassword}
                        onChangeText={confirmpassword => this.setState({confirmpassword})}
                        secureTextEntry
                    />
                <View style={{ flex: 1, justifyContent: 'center', marginTop:40, marginBottom: 10 }}>
                    <Button
                        disabled = 
                        {
                            (this.state.fullname == '')||
                            (this.state.username == '')||
                            (this.state.emailPhonenumber == '')||
                            (this.state.password == '')||
                            (this.state.confirmpassword == '')||
                            (this.state.password != this.state.confirmpassword)
                        } title="Sign Up Now"
                        onPress={() => this.handleSignUp() > this.props.navigation.navigate("Verification")}
                        //onPress={() => this.props.navigation.navigate("Verification")}
                    />
                </View>
                </View>
                </View>
                </View>
                </View>
                </View>
                </View>
            </ScrollView>
		)
	}
}
