import * as React from 'react';
import { Button, View, TextInput, ScrollView, Text, StyleSheet} from 'react-native';

export default class VerificationScreen extends React.Component {
    state = {
        OTP_Passcode: "",
    }
    
    render(){
		return(
            <ScrollView>
                <View style={styles.Verification}>
                    <Text>Key in the One-Time Password sent to your email/mobile phone</Text>
                    
                    <TextInput
                        value={this.state.OTP_Passcode}
                        onChangeText={OTP_Passcode => this.setState({OTP_Passcode})}
                        keyboardType = {'numeric'}
                    />

                    <Button
                        title="Submit"
                        onPress={() => this.props.navigation.navigate("ScanNow")}
                    /> 
                </View>
            </ScrollView>
            
            
		)
	}
}

const styles = StyleSheet.create({
    Verification: {
        flex: 1, 
        alignItems: 'center', 
        justifyContent: 'center' 
    }
});
