import * as React from 'react';
import { Button, View, Text, TextInput, TouchableOpacity, ScrollView, StyleSheet} from 'react-native';
//import * as firebase from 'firebase';
import firebase from 'firebase/app';
import "firebase/auth";
//import { firebase } from '@react-native-firebase/auth';

export default class SignUpScreen extends React.Component {
    handleSignUp = () => {
        const {emailPhonenumber,password} = this.state;
        firebase.auth().createUserWithEmailAndPassword(emailPhonenumber, password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    
    state = {
        fullname : "",
        username : "",
        emailPhonenumber : "",
        password : "",
        confirmpassword : ""
    }
    
    render(){

		return(
            <ScrollView>
                <View style={styles.container}>
                    <Text style = {styles.Instructions}> Fill in the details below</Text>

                    <TextInput
                        style={styles.Full_Name}
                        placeholder="Full Name*"
                        value={this.state.fullname}
                        onChangeText={fullname => this.setState({fullname})}
                    />

                    <TextInput
                        style={styles.Username}
                        placeholder="Username*"
                        value={this.state.username}
                        onChangeText={username => this.setState({username})}
                    />

                    <TextInput
                        style={styles.Email_Phone}
                        placeholder="Email or Phone Number"
                        value={this.state.emailPhonenumber}
                        onChangeText={emailPhonenumber => this.setState({emailPhonenumber})}
                    />

                    <Text style={(this.state.password.length < 8) ? styles.Warning_Message : styles.Password_Accepted}> 
                        {(this.state.password.length < 8) ? 'Password must be at least 8 characters' : 'Password accepted!'}
                    </Text>

                    <TextInput
                        style={styles.Password}
                        placeholder="Password*"
                        value={this.state.password}
                        onChangeText={password => this.setState({password})}
                        secureTextEntry
                    />

                    <TextInput
                        style={styles.Confirm_Password}
                        placeholder="Confirm Password*"
                        value={this.state.confirmpassword}
                        onChangeText={confirmpassword => this.setState({confirmpassword})}
                        secureTextEntry
                    />

                    <Text style={(this.state.password != this.state.confirmpassword) ? styles.Warning_Message : styles.Password_Accepted}> 
                        {(this.state.password != this.state.confirmpassword) ? 'Passwords do not match!' : 'Passwords match!'}
                    </Text>
                    
                    <TouchableOpacity
                        style = 
                        {(
                            (this.state.fullname == '')||
                            (this.state.username == '')||
                            (this.state.emailPhonenumber == '')||
                            (this.state.password == '')||
                            (this.state.confirmpassword == '')||
                            (this.state.password != this.state.confirmpassword)
                        ) ? styles.Sign_Up_Button_Disabled : styles.Sign_Up_Button_Enabled}
                        
                        disabled = 
                        {
                            (this.state.fullname == '')||
                            (this.state.username == '')||
                            (this.state.emailPhonenumber == '')||
                            (this.state.password == '')||
                            (this.state.confirmpassword == '')||
                            (this.state.password != this.state.confirmpassword)||
                            (this.state.password.length < 8)
                        } 
                        onPress={() => this.handleSignUp() > this.props.navigation.navigate("Verification")}
                        //onPress={() => this.props.navigation.navigate("Verification")}
                    >
                        <Text style = {styles.Button_Text}> Sign Up </Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>
		)
	}
}

const styles = StyleSheet.create({
    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },
    
    Instructions: {
        textAlign: 'center',
        color: "#000", 
        fontWeight: "bold", 
        fontSize: 25,
        marginTop: 50,
        marginBottom: 25,
    },

    Full_Name: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Username: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Email_Phone: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Password: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Confirm_Password: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25, 
    },

    Warning_Message: {
        color: "#FF0000",
        fontWeight: "600",
        marginTop: 0,
        marginBottom: 0,
    },

    Password_Accepted: {
        color: "#00FF00",
        fontWeight: "600",
        marginTop: 0,
        marginBottom: 0,
    },

    Sign_Up_Button_Disabled: {
        backgroundColor : '#800000',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 50,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Sign_Up_Button_Enabled: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 50,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});
