import * as React from 'react';
import {Dimensions, View} from 'react-native';
import {VictoryChart, VictoryGroup, VictoryBar, VictoryLegend, VictoryAxis} from "victory-native";

var database = firebase.database();
// export default  class PowerUsageBarChart extends React.Component {
//     render () {
const data = {
    PowerConsumed: [
        null, {x: 'Week 1', y: 20}],
    // when you want to show the ideal power usage to consume, for comparison
    //     IdealPowerConsumed: [ 
    //         {x: 'Week 1', y: 50};
    //         {x: 'Week 2', y: 80};
    // ]
};
const PowerUsageBarChart: React.FC = () => {
    return (
    <View>
        <VictoryChart>
            <VictoryAxis label = "Week"/>
            <VictoryAxis 
                dependentAxis 
                label = "Hours"
                style ={{
                    axislabel: {
                        padding: 35,
            },
            }}/>
            <VictoryGroup offset={20}>
                <VictoryBar 
                data={data.PowerConsumed}
                style={{
                    data:{fill: 'blue',
                },
                }}/>
                {/* </VictoryBar> */}
            </VictoryGroup>
            <VictoryLegend 
            x = {Dimensions.get('screen').width / 2-50}
            orientation = "horizontal"
            gutter = {20}
            data = {[
                {
                    name: 'Power Consumed',
                    symbol: {
                        fill: 'blue',
                    },
                },
            ]}
            />
        </VictoryChart>
    </View>
        );
};
export default PowerUsageBarChart;
