import * as React from 'react';
import CountDown from 'react-native-countdown-component';
import { Button, View, Text, StatusBar} from 'react-native';

function SchedulingCountdownTimerScreen({ route, navigation }) {
    console.warn(route)
    let Text_Minutes = route.params;
    return (
      <View style={styles.Set_Timer}>
      <StatusBar barStyle="dark-content" backgroundColor="#ecf0f1" />
        <Text>Countdown Timer</Text>
        <Text style = {{fontSize: 30}} > {Text_Minutes} </Text>
   
        <CountDown
          size={30}
          until={Text_Minutes*60}
          onFinish={() => alert('Finished') > navigation.navigate('TimesUp')}
          digitStyle={{backgroundColor: '#FFF', borderWidth: 2, borderColor: '#1CC625'}}
          digitTxtStyle={{color: '#1CC625'}}
          timeLabelStyle={{color: 'red', fontWeight: 'bold'}}
          separatorStyle={{color: '#1CC625'}}
          timeToShow={['H', 'M', 'S']}
          timeLabels={{m: null, s: null}}
          showSeparator
        />
   
        <Button
          title="Stop Now"
          onPress={() => navigation.navigate('TimesUp')}
        />
      </View>
    );
  }

  const styles = StyleSheet.create({
    Set_Timer: {
      backgroundColor: '#90FF33',
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center'  
    }
});

export default SchedulingCountdownTimerScreen;
