import { BarChart } from 'react-native-charts';
 import { Image, View, Text, TouchableOpacity, StyleSheet} from 'react-native';

 var database = firebase.database();

 function PastUsageBarchart({ route, navigation }) {
     let data = route.params;
     const [PowerConsumed, setPowerConsumed] = React.useState('');
     const [currenttime, setcurrenttime] = React.useState('');

     useEffect(() => {
        setInterval(() => (getDate() > readData({data})), 1000);
    });

    const readData = ({data}) => {
        firebase.database().ref('/Socket ID/' + data.slice(17,20)).once('value').then((snapshot) => {
        setPowerConsumed(snapshot.val() && snapshot.val().Power) ;
        })
    };

    const getDate = () => {
        var now = new Date();
        var time = new Date(now.getTime());
        setcurrenttime(time + "");
    }

    return (
        <View>

        <Text>Past Usgage Bar Chart </Text>

        <TouchableOpacity
                        style={styles.Navigation_Button}
                        onPress={() => navigation.navigate('Main Function')}
                    >
                        <Text style = {styles.Button_Text}> Return to Main Function </Text>
                    </TouchableOpacity>   
            <BarChart
            dataSets={[
    { 
                  fillColor: '#46b3f7', 
                data: [
                  { value: 15 },
                  { value: 10 },
                  { value: 12 },
                  { value: 11 },
                 ]
             },
            ]
        }
  graduation={1}
  horizontal={false}
  showGrid={true}
  barSpacing={5}
  style={{
    height: 300,
    margin: 15,
  }}/>
  </View>
  
);
 }

export default PastUsageBarchart;
