import * as React from 'react';
import {Dimensions, View} from 'react-native';
import {VictoryChart, VictoryGroup, VictoryBar, VictoryLegend, VictoryAxis} from "victory-native";

// var database = firebase.database();
// export default  class PowerUsageBarChart extends React.Component {
//     render () {
const data = {
    PowerConsumed: [
        null, {x: 'Week 1', y: 20}],
    // when you want to show the ideal power usage to consume, for comparison
    //     IdealPowerConsumed: [ 
    //         {x: 'Week 1', y: 50};
    //         {x: 'Week 2', y: 80};
    // ]
};
const PowerUsageBarChart = () => {
    return (
        React.createElement(
            View,
            null,
            React.createElement(
                VictoryChart,
                null,
                React.createElement(VictoryAxis, { label: "Week" }),
                React.createElement(VictoryAxis, {
                    dependentAxis: true,
                    label: "Hours",
                    style: {
                        axislabel: {
                            padding: 35
                        }
                    } }),
                React.createElement(
                    VictoryGroup,
                    { offset: 20 },
                    React.createElement(VictoryBar, {
                        data: data.PowerConsumed,
                        style: {
                            data: { fill: 'blue'
                            }
                        } })
                ),
                React.createElement(VictoryLegend, {
                    x: Dimensions.get('screen').width / 2 - 50,
                    orientation: "horizontal",
                    gutter: 20,
                    data: [{
                        name: 'Power Consumed',
                        symbol: {
                            fill: 'blue'
                        }
                    }]
                })
            )))
};
export default PowerUsageBarChart;
