import * as React from 'react';
import { View, Text, TouchableOpacity, StyleSheet} from 'react-native';

function ResetPasswordScreen({ route, navigation }) {
    console.warn(route)
    let emailphone = route.params;
  
    return (
      <View style={styles.container}>
        <Text style = {styles.Instructions}> Follow the instructions sent to 
            <Text style = {styles.Instructions_email}> {emailphone} </Text> 
        to reset password </Text>
      
        <TouchableOpacity
          style={styles.Navigation_Button}
          onPress={() => navigation.navigate('ForgetPassword')}
        >
          <Text style = {styles.Button_Text}> Change Email/Phone Number? </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.Navigation_Button}
          onPress={() => navigation.navigate('Login')}
        >
          <Text style = {styles.Button_Text}> Proceed to login again </Text>
        </TouchableOpacity>
      </View>
    );
  }

  const styles = StyleSheet.create({
    Instructions: {
      marginLeft: 25,
      marginRight: 25,
      marginTop: 50,
      marginBottom: 25,
      color: "#000", 
      fontWeight: "bold", 
      fontSize: 20
    },

    Instructions_email: {
        color: "#000080", 
        fontWeight: "500", 
        fontSize: 20
      },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Navigation_Button: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 25,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});

export default ResetPasswordScreen;
