import * as React from 'react';
import {useEffect} from "react";
import { View, Text, TextInput, FlatList, TouchableOpacity, ScrollView, StyleSheet} from 'react-native';
import firebase from 'firebase/app';
import "firebase/auth";
import 'firebase/database';

function PastUsageScreen({ route, navigation }) {
    let data = route.params;
    const [Power, setPower] = React.useState('');
    const [currenttime, setcurrenttime] = React.useState('');

    useEffect(() => {
        setInterval(() => (getDate() > readData({data})), 3000);
    });

    const readData = ({data}) => {
        firebase.database().ref('/Socket ID/' + data.slice(17,20)).once('value').then((snapshot) => {
        setPower(snapshot.val() && snapshot.val().setPower) ;
        })
    };

    const getDate = () => {
        var now = new Date();
        var time = now.getHours() + ":" + now.getMinutes();
        setcurrenttime(time)
    }

    return (
        <ScrollView>
            <View style={styles.container}>
                <Text style = {styles.Instructions}> Records of Past Usage (Date/Watts) {user_email}</Text>

                <FlatList
                    data = {Power}
                    renderItem = {({ item, index}) =>{
                        return(
                            <Text style = {styles.Instructions}> 
                                {Power}
                            </Text>
                        )
                    }}
                >

                </FlatList>
                
                
                <TouchableOpacity
                    style={styles.Navigation_Button}
                    onPress={() => navigation.navigate('Main Function')}
                >
                    <Text style = {styles.Button_Text}> Return to Main Function </Text>
                </TouchableOpacity>   
                
            </View>
        </ScrollView>
    );
  }

  const styles = StyleSheet.create({
    Instructions: {
      textAlign: 'center',
      marginLeft: 25,
      marginRight: 25,
      marginTop: 50,
      marginBottom: 25,
      color: "#000", 
      fontWeight: "bold", 
      fontSize: 25
    },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Navigation_Button: {
      backgroundColor : '#000080',
      borderRadius : 4,
      height: 52,
      width : 300,
      marginTop: 25,
      marginBottom: 250,
      alignItems: 'center',
      justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }

});

export default PastUsageScreen;
