import React, { useState, useEffect } from 'react';
import {Button, View, Text, StyleSheet, TouchableOpacity} from 'react-native';
import { BarCodeScanner } from 'expo-barcode-scanner';

function CameraScreen( {navigation} ) {
    const [hasPermission, setHasPermission] = useState(null);
    const [scanned, setScanned] = useState(false);

    useEffect(() => {
        (async () => {
          const { status } = await BarCodeScanner.requestPermissionsAsync();
          setHasPermission(status === 'granted');
        })();
      }, []);

    const handleBarCodeScanned = () => {
        setScanned(true);
        navigation.navigate("Connected")
    };

    if (hasPermission === null) {
        return <Text>Requesting for camera permission</Text>;
    }
    if (hasPermission === false) {
        return <Text>No access to camera</Text>;
    }
    
    return (
    <View style={styles.container}>
        <Text style = {styles.Instructions}> Look for a socket with a QR code</Text>

        <BarCodeScanner
            onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
            style={styles.camerasize}
        />
        {scanned && 
            <TouchableOpacity
                style={styles.Scan_Again_Button}
                onPress={() => setScanned(false)}
            >
                <Text style = {styles.Button_Text}> Press to scan again </Text>
            </TouchableOpacity>
        }

        <Button
            title="Dummy button to move to not connected screen"
            onPress={() => navigation.navigate("Not Connected")}
        />

    </View>
      
    );
  }

const styles = StyleSheet.create({
    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Instructions: {
        textAlign: 'center',
        marginLeft: 25,
        marginRight: 25,
        marginTop: 10,
        marginBottom: 25,
        color: "#000", 
        fontWeight: "bold", 
        fontSize: 25
    },

    camerasize:{
        height: 300,
        width: 300,
    },

    Scan_Again_Button: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 25,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});

export default CameraScreen;
