import * as React from 'react';
import { View, Text, TextInput, TouchableOpacity, ScrollView, StyleSheet} from 'react-native';
import firebase from 'firebase/app';
import "firebase/auth";
import 'firebase/database';

//database = firebase.database(); 
 
//var ref = database.ref('Socket ID'); 
//ref.on('value', gotData, errData); 
 
//function gotData(data){ 
//  console.log(data); 
//} 
 
//function errData(err){ 
//  console.log('Error'); 
//  console.log(err); 
//}
function PastUsageScreen({ route, navigation }) {
    let data = route.params;
    const [user_email, setuser_email] = React.useState('');

    const handlePrint = ({data}) => {
            // [START rtdb_social_single_value_read]
            firebase.database().ref('/Socket ID/' + '/002/').once('value').then((snapshot) => {
              var user_email = (snapshot.val() && snapshot.val().user_email) || 'Anonymous' ;
              console.log(user_email)

              setuser_email(user_email);
            });
            // [END rtdb_social_single_value_read]   
    }


    return (
        <ScrollView>
            <View style={styles.container}>
                <Text style = {styles.Instructions}> Records of Past Usage (Date/Watts) {user_email}</Text>

                <TouchableOpacity
                    style={styles.Navigation_Button}
                    onPress={() => handlePrint({data})}
                >
                    <Text style = {styles.Button_Text}> Return to Main Function </Text>
                </TouchableOpacity>   

                <Text style = {styles.Instructions}> My email is {user_email}</Text>
                
                
                <TouchableOpacity
                    style={styles.Navigation_Button}
                    onPress={() => navigation.navigate('Main Function')}
                >
                    <Text style = {styles.Button_Text}> Return to Main Function </Text>
                </TouchableOpacity>   
                
            </View>
        </ScrollView>
    );
  }

  const styles = StyleSheet.create({
    Instructions: {
      textAlign: 'center',
      marginLeft: 25,
      marginRight: 25,
      marginTop: 50,
      marginBottom: 25,
      color: "#000", 
      fontWeight: "bold", 
      fontSize: 25
    },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Navigation_Button: {
      backgroundColor : '#000080',
      borderRadius : 4,
      height: 52,
      width : 300,
      marginTop: 25,
      marginBottom: 250,
      alignItems: 'center',
      justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }

});

export default PastUsageScreen;
