import * as React from 'react';
import { View, TextInput, ScrollView, StyleSheet, TouchableOpacity, Text} from 'react-native';
import firebase from 'firebase/app';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import "firebase/auth";

function LoginScreen({ navigation }) {
    const [username, setusername] = React.useState('');
    const [password, setpassword] = React.useState('');

    const uiConfig = {
        // Popup signin flow rather than redirect flow.
        signInFlow: 'popup',
        // We will display Google and Facebook as auth providers.
        signInOptions: [
          firebase.auth.GoogleAuthProvider.PROVIDER_ID,
          firebase.auth.FacebookAuthProvider.PROVIDER_ID
        ]
      };

    const handleLogin = () => {
        firebase.auth().signInWithEmailAndPassword(username, password)
        .then(() => {
            //If all issues resolved
            var user = firebase.auth().currentUser;
            var UserVerified = user.emailVerified;

            if(UserVerified){
                navigation.navigate("Scan Now");
            }
            
            else{
                alert("Email not verified, please try again")
            }
        }) 
        
        .catch(function(error) {
            // Handle Errors here.
            var errorMessage = error.message;
            
            alert(errorMessage);
            console.log(error);
        });
    };

    return (
        
    <div>
        <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={firebase.auth()}/>
      </div>
    );
  }

  const styles = StyleSheet.create({
    Instructions: {
        textAlign: 'center',
        marginTop: 50,
        marginBottom: 25,
        color: "#000", 
        fontWeight: "bold", 
        fontSize: 25
    },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Key_In_Username: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Key_In_Password: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 25,
    },

    Navigation_Text: {
        alignSelf: "center",
        marginTop: 5,
        marginBottom: 5
    },

    Sign_In_Button_Disabled: {
        backgroundColor : '#800000',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 50,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Sign_In_Button_Enabled: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 50,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});

export default LoginScreen;
