import * as React from 'react';
import { View, Text, TouchableOpacity, StyleSheet} from 'react-native';

export default class MainFunctionScreen extends React.Component {
	render(){
		return(
            <View style={styles.container}>
                <Text style = {styles.Instructions}> Control your smart socket here </Text>

                <TouchableOpacity
                    style={styles.Navigation_Buttons}
                    onPress={() => this.props.navigation.navigate("OnOff")}
                >
                    <Text style = {styles.Button_Text}> Switch on/off socket </Text>
                </TouchableOpacity>   

                <TouchableOpacity
                    style={styles.Navigation_Buttons}
                    onPress={() => this.props.navigation.navigate("SetTimer")}
                >
                    <Text style = {styles.Button_Text}> Switch on/off socket via schedule </Text>
                </TouchableOpacity>   
 
                <TouchableOpacity
                    style={styles.Navigation_Buttons}
                    onPress={() => this.props.navigation.navigate("Home")}
                >
                    <Text style = {styles.Button_Text}> Log Out </Text>
                </TouchableOpacity>   

            </View>
		)
	}
}

const styles = StyleSheet.create({
    Instructions: {
        textAlign: 'center',
        marginTop: 50,
        marginBottom: 25,
        color: "#000", 
        fontWeight: "bold", 
        fontSize: 25
    },

    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Navigation_Buttons: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 25,
        marginBottom: 25,
        alignItems: 'center',
        justifyContent: 'center'
    },

    Button_Text: {
        color: "#FFF",
        fontWeight: "600"
    }
});
