import * as React from 'react';
import { Button, View, Text, TextInput, ScrollView, StyleSheet} from 'react-native';
//import * as firebase from 'firebase';
import firebase from 'firebase/app';
import "firebase/auth";
//import { firebase } from '@react-native-firebase/auth';

export default class SignUpScreen extends React.Component {
    handleSignUp = () => {
        const {emailPhonenumber,password} = this.state;
        firebase.auth().createUserWithEmailAndPassword(emailPhonenumber, password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    
    state = {
        fullname : "",
        username : "",
        emailPhonenumber : "",
        password : "",
        confirmpassword : ""
    }
    
    render(){
        if(this.state.password != this.state.confirmpassword){
            <View style={styles.Instructions}>
                <Text>Please Confirm Password again</Text>
            </View>
            
        }
        else{
            <View style={styles.Instructions}>
                <Text>Please Confirm Password again</Text>
            </View>
        }

		return(
            <ScrollView>
                <View style={styles.Instructions}>
                    <Text>Fill IN THE DETAILS BELOW</Text>

                <View style={styles.Full_Name}>    
                    <TextInput
                        placeholder="Full Name*"
                        value={this.state.fullname}
                        onChangeText={fullname => this.setState({fullname})}
                    />
                <View style={styles.Username}>
                    <TextInput
                        placeholder="Username*"
                        value={this.state.username}
                        onChangeText={username => this.setState({username})}
                    />
                <View style={styles.Email_Phone}>
                    <TextInput
                        placeholder="Email or Phone Number"
                        value={this.state.emailPhonenumber}
                        onChangeText={emailPhonenumber => this.setState({emailPhonenumber})}
                    />
                <View style={styles.Password}>
                    <TextInput
                        placeholder="Password*"
                        value={this.state.password}
                        onChangeText={password => this.setState({password})}
                        secureTextEntry
                    />
                <View style={styles.Confirm_Password}>
                    <TextInput
                        placeholder="Confirm Password*"
                        value={this.state.confirmpassword}
                        onChangeText={confirmpassword => this.setState({confirmpassword})}
                        secureTextEntry
                    />

                <View style={styles.Confirm_Password}>
                    <Text> 
                        {(this.state.password != this.state.confirmpassword) ? 'Please Confirm Password again' : ''}
                    </Text>
                    
                <View style={styles.Sign_Up_Button}>
                    <Button
                        disabled = 
                        {
                            (this.state.fullname == '')||
                            (this.state.username == '')||
                            (this.state.emailPhonenumber == '')||
                            (this.state.password == '')||
                            (this.state.confirmpassword == '')||
                            (this.state.password != this.state.confirmpassword)
                        } title="Sign Up Now"
                        onPress={() => this.handleSignUp() > this.props.navigation.navigate("Verification")}
                        //onPress={() => this.props.navigation.navigate("Verification")}
                    />
                </View>
                </View>
                </View>
                </View>
                </View>
                </View>
                </View>
                </View>
            </ScrollView>
		)
	}
}

const styles = StyleSheet.create({
    Instructions: {
        flex: 1, 
        alignItems: 'center', 
        marginTop: 150, 
        marginBottom: 30 
    },

    Full_Name: {
        flex: 1, 
        justifyContent: 'center', 
        marginTop:40, 
        marginBottom: 10 
    },

    Username: {
        flex: 1, 
        justifyContent: 'center', 
        marginTop:40, 
        marginBottom: 10
    },

    Email_Phone: {
        flex: 1, 
        justifyContent: 'center', 
        marginTop:40, 
        marginBottom: 10
    },

    Password: {
        flex: 1, 
        justifyContent: 'center', 
        marginTop:40, 
        marginBottom: 10 
    },

    Confirm_Password: {
        flex: 1, 
        justifyContent: 'center',
        marginTop:40, 
        marginBottom: 10  
    },

    Sign_Up_Button: {
        flex: 1, 
        justifyContent: 'center', 
        marginTop:40, 
        marginBottom: 10 
    },
});
