import * as React from 'react';
import { Button, View, TextInput, ScrollView, StyleSheet} from 'react-native';
import firebase from 'firebase/app';
import "firebase/auth";
import { TouchableOpacity } from 'react-native-gesture-handler';

export default class LoginScreen extends React.Component {
    
    handleLogin = () => {
        const {username,password} = this.state;
        firebase.auth().signInWithEmailandPassword(username,password).catch(error => this.setState({errorMessage: error.message})
        );
    };
    state = {
        username: "",
        password: ""
    }   
    
    render(){
		return(
            <ScrollView>
                

                <View style={styles.container}>
                    

                    <TextInput
                        style={styles.Key_In_Username}
                        placeholder="Username"
                        value={this.state.username}
                        onChangeText={username => this.setState({username})}
                    />
                    <TextInput
                        style={styles.Key_In_Password}
                        placeholder="Password"
                        value={this.state.password}
                        onChangeText={password => this.setState({password})}
                        secureTextEntry
                    />

                    <TouchableOpacity>
                        style={styles.Sign_In_Button}
                        
                        onPress={() => this.props.navigation.navigate("ScanNow")}
                        disabled = {(this.state.password== '')||(this.state.username == '')}
                    </TouchableOpacity>    
                        
                </View>
            </ScrollView>
		)
	}
}

const styles = StyleSheet.create({
    container: {
        flex : 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },

    Key_In_Username: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 50,
        marginBottom: 25,
    },

    Key_In_Password: {
        height: 50, 
        width : 300,
        borderColor: 'gray', 
        borderWidth: 1,
        marginTop: 25,
        marginBottom: 50,
    },

    Sign_In_Button: {
        backgroundColor : '#000080',
        borderRadius : 4,
        height: 52,
        width : 300,
        marginTop: 50,
        marginBottom: 50,
        alignItems: 'center',
        justifyContent: 'center'
    }
});
